CREATE FUNCTION afd.transiciones(automata numeric) RETURNS TABLE(ruta integer[], id numeric, nombre character varying, descripcion character varying, estado character varying, funcion character varying, parametros character varying, eventos character varying, profundidad integer, orden numeric, ciclica boolean)
    LANGUAGE plpgsql
    AS $$
begin
    return query
    WITH RECURSIVE nodes(path, r_id, r_nombre, r_descripcion, r_estado, funcion, parametros, eventos, depth, orden, cycle) AS ( 
        SELECT 
            ARRAY[cast(grafo.ieag_hijo as integer)],
            grafo.ieag_hijo, regla.r_nombre, regla.r_descripcion, 
            restado.en_valor,
            regla.r_funcion, grafo.ieag_parametro, 
            grafo.ieag_evento, 
            1, grafo.ieag_secuencia,
            false
        FROM
            afd.inst_elem_auto_grafo AS grafo,
            afd.regla AS regla,
            mmo.entidad AS restado
        WHERE
            grafo.ieag_automata = automata AND 
            grafo.ieag_estado in (900,905) AND
            restado.en_id = grafo.ieag_estado AND 
            regla.r_id = grafo.ieag_regla
    UNION ALL 
        SELECT 
            path  || cast(grafo.ieag_hijo as integer),
            grafo.ieag_hijo, regla.r_nombre, regla.r_descripcion, 
            restado.en_valor,
            regla.r_funcion, grafo.ieag_parametro, 
            grafo.ieag_evento, 
            nd.depth + 1, grafo.ieag_secuencia,
            grafo.ieag_hijo = ANY(path)
        FROM 
            afd.inst_elem_auto_grafo AS grafo, 
            afd.regla AS regla,
            mmo.entidad AS restado,
            nodes AS nd
        WHERE 
            grafo.ieag_padre = nd.r_id AND 
            regla.r_id = grafo.ieag_regla AND 
            restado.en_id = grafo.ieag_estado 
            AND NOT cycle 
    ) 
    SELECT * FROM nodes where cycle = false  union SELECT * FROM nodes where cycle = true order by depth, orden;

end
$$;


