CREATE FUNCTION cac.buscarpersona_por_anexo(panexo text) RETURNS TABLE("pId" numeric, "pNombre" character varying, "pApellido" character varying, "insId" numeric, "insNombre" character varying, "insDireccion" character varying, "pFono" character varying, "pAnexo" character varying)
    LANGUAGE plpgsql
    AS $$
begin
    return query
    select 
        p_id as pId,
        p_nombre as pNombre,
        p_apellido as pApellido,
        uia.ins_id as insId,
        uia.ins_nombre as insNombre,
        uia.ins_direccion as insDireccion, 
        (select ul_valor from cac.usuario_login where ul_usuario = cac.usuario.u_id and ul_tipo = 861)  as pFono,
        (select ul_valor from cac.usuario_login where ul_usuario = cac.usuario.u_id and ul_tipo = 862)  as pAnexo
    from cac.persona
            join cac.usuario
                on p_id = u_persona
                join (
                    select uia_usuario, ins_id, ins_nombre, ins_direccion
                        from org.usuario_ins_acceso 
                                join org.inst_acceso
                                    on uia_acceso = ia_id
                                        join org.inst
                                            on ia_instalacion = ins_id
                        group by uia_usuario, ins_id, ins_nombre, ins_direccion
                    ) as uia
                    on uia.uia_usuario = u_id
                    join (
                        select ul_usuario, max(CASE WHEN ul_tipo = 861 THEN ul_valor END) as fono, max(CASE WHEN ul_tipo = 862 THEN ul_valor END) as anexo
                        from cac.usuario_login
                        where ul_tipo in (861, 862)
                        group by ul_usuario
                    ) as ul
                        on ul.ul_usuario = u_id
                        
    where (ul.fono = panexo or  ul.anexo = panexo)

    order by 2, 3;
    
    -- 861;"Telefono"
    -- 10067;"Anexo"


end
$$;


