CREATE FUNCTION cac.regpersona(jpersona jsonb) RETURNS numeric
    LANGUAGE plpgsql
    AS $$

   DECLARE

    
    tmp_persona NUMERIC;
    
    pId numeric;
    pRut character varying;
    pDv character varying;
    pNombre character varying;
    pApellido character varying;
    pSexo numeric;
    pCorreo character varying;
    pFono character varying;

    tmp record;

    login character varying;
    num numeric;
    
    BEGIN


    pId := NULL;
    pRut := NULL;
    pDv := NULL;
    pNombre := NULL;
    pApellido := NULL;
    pCorreo := ' ';
    pSexo := NULL;
    pFono := ' ';




    IF jpersona->>'pRut' IS NOT NULL and char_length(jpersona->>'pRut') > 0 THEN
        pRut := jpersona->>'pRut';
    END IF;

    IF jpersona->>'pDv' IS NOT NULL and char_length(jpersona->>'pDv') > 0 THEN
        pDv := cast(jpersona->>'pDv' as numeric);
    END IF;
    

    IF jpersona->>'pId' IS NOT NULL and char_length(jpersona->>'pId') > 0 THEN
        pId := cast(jpersona->>'pId' as numeric);
    END IF;

    IF jpersona->>'pNombre' IS NOT NULL and char_length(jpersona->>'pNombre') > 0 THEN
        pNombre := jpersona->>'pNombre';
    END IF;

    IF jpersona->>'pApellido' IS NOT NULL and char_length(jpersona->>'pApellido') > 0 THEN
        pApellido := jpersona->>'pApellido';
    END IF;

    IF jpersona->>'pSexo' IS NOT NULL and char_length(jpersona->>'pSexo') > 0 THEN
        pSexo := cast(jpersona->>'pSexo' as numeric);
    END IF;

    IF jpersona->>'pCorreo' IS NOT NULL  and char_length(jpersona->>'pCorreo') > 0 THEN
        pCorreo := jpersona->>'pCorreo';
    END IF;

    IF jpersona->>'pFono' IS NOT NULL and char_length(jpersona->>'pFono') > 0  THEN
        pFono := jpersona->>'pFono';
    END IF;


    -- validar rut
    IF pRut IS NOT NULL THEN

        IF pDv IS NULL THEN
            RAISE EXCEPTION 'Error debe registrar el digito verificador'
                        USING HINT = 'Ingrese el digito verificador';
            return -1;
        ELSE
        BEGIN
            IF lib.calculardv(pRut) <> UPPER(pDv) THEN
                
                RAISE EXCEPTION 'Error digito verificador no valido'
                        USING HINT = 'Digito verificador no valido';

                return -1;
            END IF;
        END;
        END IF;

    END IF;


    
    IF pId IS NOT NULL THEN
        -- actualiza
        UPDATE cac.persona
            set p_apellido = pApellido
            , p_nombre = pNombre
            -- , p_sexo = pSexo
            , p_correo = pCorreo
            --, p_fono = pFono
        WHERE p_id = pId;

        --tmp_persona := pId;

    ELSE
    

        -- crear persona
        INSERT INTO cac.persona( p_apellido, p_correo,  p_id, p_nombre, p_tratamiento)
            VALUES (pApellido, pCorreo,  NEXTVAL('cac.seq_persona'), pNombre, 20); -- 311 masculino, 20 sr 
        SELECT CURRVAL('cac.seq_persona') INTO pId;
        

        -- crear login de usuario
        SELECT  lower(substring(pNombre from 1 for 1) || '_' || split_part( pApellido, ' ' , 1 )) INTO login;
        -- si el login existe crea uno distinto con un numero al final
        SELECT count(*) INTO num from cac.usuario where u_login like login || '%';
        IF num > 0 THEN
        BEGIN
            select login || cast(num as character varying) into login;
        END;
        END IF;
        
        -- crear usuario de la persona
        INSERT INTO cac.usuario( u_id, u_login, u_password, u_persona, u_vigente )
            VALUES (NEXTVAL('cac.seq_usuario'), login, '1234', pId, 1 );

    END IF;


    IF pFono IS NOT NULL THEN
        select cac.regpersona_atr( pId, 14, pFono ) into tmp;
    END IF;

    IF pSexo IS NOT NULL THEN
        select cac.regpersona_atr( pId, 18, cast(pSexo as character varying) ) into tmp;
    END IF;

    IF pRut IS NOT NULL AND pDv IS NOT NULL THEN
        select cac.regpersona_atr( pId, 13, pRut || '-' || pDv) into tmp;
    END IF;
    

    return pId;
          
    END;
    $$;


