    IF tmp_paId IS NOT NULL THEN
    BEGIN

        -- cambia bodegas y departamentos al nuevo propietario
        UPDATE cac.persona_atr 
            SET PA_VALOR = paValor
        WHERE PA_ID = tmp_paId;
        
