CREATE FUNCTION lib.tabularserie(datos text, delimitador character varying DEFAULT ','::character varying) RETURNS SETOF numeric
    LANGUAGE plpgsql
    AS $$
declare
    elementos text[];
begin
    elementos := string_to_array(datos, delimitador);
    for i in array_lower(elementos, 1) .. array_upper(elementos, 1) loop
        return next cast(elementos[i] as numeric);
    end loop;
    return;
end
$$;


