    LANGUAGE plpgsql
    AS $$
declare
    tId numeric;
    tejecutada numeric;
    tarchivo numeric;
    dmlsqlite text;
    lenc integer;
    cambio boolean;
begin
    -- no informa transacciones negativas a sincronizar
    if(new.en_id < 0) then
        return new;
    end if;

    cambio := false;
    dmlsqlite := 'update ''mmo.entidad'' set ';
    lenc := length(dmlsqlite);
       if (new.en_codigo <> old.en_codigo ) then
       dmlsqlite := concat(dmlsqlite, 'en_codigo=''', new.en_codigo, ''', ');
  end if;
   if (new.en_dependiente <> old.en_dependiente ) then
       dmlsqlite := concat(dmlsqlite, 'en_dependiente=', new.en_dependiente, ', ');
  end if;
   if (new.en_dominio <> old.en_dominio ) then
       dmlsqlite := concat(dmlsqlite, 'en_dominio=', new.en_dominio, ', ');
  end if;
   if (new.en_id <> old.en_id ) then
       dmlsqlite := concat(dmlsqlite, 'en_id=', new.en_id, ', ');
  end if;
   if (new.en_padre <> old.en_padre ) then
       dmlsqlite := concat(dmlsqlite, 'en_padre=', new.en_padre, ', ');
  end if;
   if (new.en_valor <> old.en_valor ) then
       dmlsqlite := concat(dmlsqlite, 'en_valor=''', new.en_valor, ''', ');
  end if;

    if(length(dmlsqlite) > lenc) then
        cambio := true;
    end if;
    dmlsqlite := substring(dmlsqlite,1,length(dmlsqlite)-2)  || ' where en_id = ' || old.en_id || ';';

    tejecutada := 0;
    tarchivo := 0;
    if(cambio) then
        select nextval('trx.seq_trans')::regclass into tId;
       -- inserta la transaccion en la tabla trans
       insert into trx.trans(t_id, t_sql, t_archivo, t_hora, t_historial, t_ejecutada) 
      values(tId, dmlsqlite, tarchivo, now(), 0, tejecutada);
    end if;
  return new;
end;
$$;


