CREATE FUNCTION mmo.regatributo(jsoninput jsonb, OUT salida boolean, OUT id numeric) RETURNS record
    LANGUAGE plpgsql
    AS $$

   DECLARE

    aId  numeric;
    aNombre character varying;
    aAyuda character varying;
    aEtiqueta character varying;
    aTipoDato numeric;
    aLargo numeric;
    aRequerido numeric;
    aTraduccion numeric;
    

    
    BEGIN

    aId := NULL;
    aNombre := NULL;
    aAyuda := NULL;
    aEtiqueta := NULL;
    aTipoDato := NULL;
    aLargo := NULL;
    aRequerido := NULL;
    aTraduccion := NULL;
    
    IF jsonInput->>'aId' IS NOT NULL THEN
        aId := jsonInput->>'aId';
    END IF;

    IF jsonInput->>'aNombre' IS NOT NULL THEN
        aNombre := jsonInput->>'aNombre';
    ELSE
    BEGIN
        RAISE EXCEPTION ' Error'
            USING HINT = 'Nombre no valido';

    END;
    END IF;

    IF jsonInput->>'aEtiqueta' IS NOT NULL THEN
        aEtiqueta := jsonInput->>'aEtiqueta';
    ELSE
    BEGIN
        RAISE EXCEPTION ' Error'
            USING HINT = 'Etiqueta no valida';

    END;
    END IF;

    IF jsonInput->>'aAyuda' IS NOT NULL THEN
        aAyuda := jsonInput->>'aAyuda';
    ELSE
    BEGIN
        RAISE EXCEPTION ' Error'
            USING HINT = 'Ayuda no valida';

    END;
    END IF;

    IF jsonInput->>'aTipoDato' IS NOT NULL THEN
        aTipoDato := jsonInput->>'aTipoDato';
    ELSE
    BEGIN
        RAISE EXCEPTION ' Error'
            USING HINT = 'Tipo Dato no valido';

    END;
    END IF;


    
    IF jsonInput->>'aLargo' IS NOT NULL THEN
        aLargo := cast(jsonInput->>'aLargo' as numeric);
    END IF;

    IF jsonInput->>'aRequerido' IS NOT NULL THEN
        aRequerido := cast(jsonInput->>'aRequerido' as numeric);
    END IF;

    IF jsonInput->>'aTraduccion' IS NOT NULL THEN
        aTraduccion := cast(jsonInput->>'aTraduccion' as numeric);
    END IF;


    

    if aId IS NOT NULL THEN
    BEGIN


        UPDATE mmo.atributo
            SET a_ayuda = aAyuda,
            a_etiqueta = aEtiqueta,
            a_largo = aLargo,
            a_nombre = aNombre,
            a_requerido = aRequerido,
            a_tipo_dato = aTipoDato,
            a_traduccion = aTraduccion 
        WHERE a_id = aId ;

        id := aId;
    END;
    ELSE
    BEGIN

        INSERT INTO mmo.atributo( a_ayuda, a_etiqueta, a_id, a_largo, a_nombre, a_requerido, a_tipo_dato, a_traduccion)
            VALUES (aAyuda, aEtiqueta, NEXTVAL('mmo.seq_atributo'), aLargo, aNombre, aRequerido, aTipoDato,  aTraduccion);

        SELECT CURRVAL('mmo.seq_atributo') INTO id; 

    END;
    END IF;
    
        
    
    salida:= true;

          
    END;
    $$;


