--

CREATE FUNCTION mmo.regentidad(jsoninput jsonb, OUT salida boolean, OUT id numeric) RETURNS record
    LANGUAGE plpgsql
    AS $$

   DECLARE

    
    r1 record;
    r2 record;


    enCodigo  character varying;
    enDominio numeric;
    enId numeric;
    enPadre numeric;
    enValor character varying;
    enDependiente numeric;
    enDependiente_old numeric;

    datajson jsonb;



    
    BEGIN

    enCodigo := NULL;
    enDominio := NULL;
    enId := -1;
    enPadre := NULL;
    enValor := NULL;
    enDependiente := 0;
    

    
    IF jsonInput->>'enCodigo' IS NOT NULL THEN
        enCodigo := jsonInput->>'enCodigo';
    END IF;

    IF jsonInput->>'enDominio' IS NOT NULL THEN
        enDominio := cast(jsonInput->>'enDominio' as numeric);
    ELSE
    BEGIN
        RAISE EXCEPTION ' Error'
            USING HINT = 'Dominio no valido';

    END;
    END IF;

    IF jsonInput->>'enId' IS NOT NULL THEN
        enId := cast(jsonInput->>'enId' as numeric);
    END IF;

    IF jsonInput->>'enPadre' IS NOT NULL THEN
        enPadre := cast(jsonInput->>'enPadre' as numeric);
    END IF;

    IF jsonInput->>'enValor' IS NOT NULL THEN
        enValor := jsonInput->>'enValor';
    ELSE
        RAISE EXCEPTION ' Error'
            USING HINT = 'Valor no valido';
    END IF;


    IF jsonInput->>'enDependiente' IS NOT NULL THEN
        enDependiente := cast(jsonInput->>'enDependiente' as numeric);
    END IF;
    

    if enId <> -1 THEN 
    BEGIN

        IF enDependiente = 1 THEN
        BEGIN

            -- verifica el valor anterior
            SELECT en_dependiente INTO enDependiente_old
                from mmo.entidad
            where en_id = enId;

            IF enDependiente_old = 0 THEN
            BEGIN
                -- crea dominio equivalente y deja la entidad apuntando al nuevo dominio
                INSERT INTO mmo.dominio(dm_acronimo, dm_atr_json, dm_crea_nuevo, dm_descripcion, dm_id, dm_jerarquia, dm_nombre, dm_padre)
                    VALUES (upper(substr(enValor,1,2)), '', 'N', enValor, NEXTVAL('mmo.seq_dominio'), 0, enValor, enDominio);

                SELECT CURRVAL('mmo.seq_dominio') INTO enDominio;
            END;
            END IF; 
            

        
        END;
        END IF;

        UPDATE mmo.entidad
            SET en_dominio = enDominio,
            en_valor = enValor,
            en_padre = enPadre,
            en_codigo = enCodigo,
            en_dependiente = enDependiente
        WHERE en_id = enId ;
    END;
    ELSE
    BEGIN


        IF enDependiente = 1 THEN
        BEGIN
            -- crea dominio equivalente y deja la entidad apuntando al nuevo dominio
            -- por defecto extiende eleemnto y entidad modificar
            INSERT INTO mmo.dominio(dm_acronimo, dm_atr_json, dm_crea_nuevo, dm_descripcion, dm_id, dm_jerarquia, dm_nombre, dm_padre)
                VALUES (upper(substr(enValor,1,2)), '{"Extiende": ["Elemento", "Entidad"]}', 0, enValor, NEXTVAL('mmo.seq_dominio'), 0, enValor, enDominio);

            SELECT CURRVAL('mmo.seq_dominio') INTO enDominio; 
        END;
        END IF;
        
        INSERT INTO mmo.entidad(en_id, en_dominio, en_valor, en_padre, en_codigo)
                    VALUES (NEXTVAL('mmo.seq_entidad'), enDominio, enValor, enPadre, enCodigo); 

        SELECT CURRVAL('mmo.seq_entidad') INTO enId; 

    END;
    END IF;


    
    salida:= true;
    -- return 'true';
          
    END;
    $$;


