-- Name: get_children(numeric); Type: FUNCTION; Schema: org; Owner: rol_iso_app
--

CREATE FUNCTION org.get_children(elemento numeric) RETURNS json
    LANGUAGE plpgsql
    AS $$
DECLARE
result json;
BEGIN
SELECT array_to_json(array_agg(row_to_json(t))) INTO result
from (
    WITH RECURSIVE nodes(path, r_id, r_nombre, r_estado, funcion, parametros, eventos, depth, orden, cycle) AS ( 
        SELECT 
            ARRAY[cast(grafo.ieag_hijo as integer)],
            grafo.ieag_hijo, regla.r_descripcion, 
            restado.en_valor,
            regla.r_funcion, grafo.ieag_parametro, 
            grafo.ieag_evento, 
            1, grafo.ieag_secuencia,
            false
        FROM
            org.inst_elem_auto_grafo AS grafo,
            org.regla AS regla,
            mmo.entidad AS restado
        WHERE
            grafo.ieag_automata = elemento AND 
            grafo.ieag_estado = 900 AND
            restado.en_id = grafo.ieag_estado AND 
            regla.r_id = grafo.ieag_regla
    UNION ALL 
        SELECT 
            path  || cast(grafo.ieag_hijo as integer),
            grafo.ieag_hijo, regla.r_descripcion, 
            restado.en_valor,
            regla.r_funcion, grafo.ieag_parametro, 
            grafo.ieag_evento, 
            nd.depth + 1, grafo.ieag_secuencia,
            grafo.ieag_hijo = ANY(path)
        FROM 
            org.inst_elem_auto_grafo AS grafo, 
            org.regla AS regla,
            mmo.entidad AS restado,
            nodes AS nd
        WHERE 
            grafo.ieag_padre = nd.r_id AND 
            regla.r_id = grafo.ieag_regla AND 
            restado.en_id = grafo.ieag_estado  --AND
            --elemento = nd.path[array_upper(nd.path,1)]            
            AND NOT cycle 
    ) 
    SELECT * FROM nodes where cycle = false union SELECT * FROM nodes where cycle = true order by depth, orden
    ) t;
RETURN result;
END;
$$;


