    LANGUAGE plpgsql
    AS $$
begin
    return query
    select t.ie_componente, t.ie_controlador, t.ie_valor, t.u_id, t.u_login, t.ie_nombre, t.ie_instalacion, t.tipo
        from ( 
            select ie2.ie_id as ie_componente, ie1.ie_id as ie_controlador, ie2.ie_valor, u_id, u_login, ie1.ie_valor as ie_nombre, ie1.ie_instalacion, cast('Elemento' as character varying) tipo
