                        join org.inst_elem ie2
                            on ie2.ie_id = r1.er_hijo
                            join cac.usuario
                                on ie1.ie_id = u_elemento
            where ii_acceso is null
                and ii_elemento is not null 
                and ii_perfil = perfil
                and ii_vista = vista
            union
            select ia_id, ie_id, ie_valor, u_id, u_login, ia_nombre, ia_instalacion, cast('Acceso' as character varying)
