CREATE FUNCTION org.qinterface(insid numeric, perfil numeric, vista numeric, nivelinicio numeric, nivelfin numeric) RETURNS TABLE("iiId" numeric, "iiNombre" character varying, "iiPlantilla" character varying, "iiParametros" jsonb, "iiClase" character varying, "iiProcesador" character varying, "iiRuta" integer[])
    LANGUAGE plpgsql
    AS $$
begin
    return query
    select ins_id, ii_nombre,   ii_plantilla, ii_parametros, cast(tipo as character varying) ,  ii_procesador, jerarquia."ruta"
        from ( 
            select ins_id as inslatacion, ins_id, ins_nombre, ins_direccion, ins_tipo, ii_plantilla, ii_parametros, 'Instalacion' as tipo, 1 as orden, ii_procesador, ii_nombre
            from org.inst_interface join org.inst
                on ii_instalacion = ins_id
            where ii_acceso is null
                and ii_elemento is null
                and ii_perfil = perfil
                and ii_vista = vista
            union
            select ie_instalacion as inslatacion, ie_id, ie_valor, ie_descripcion, ie_tipo, ii_plantilla, ii_parametros, 'Elemento' as tipo, 2, ii_procesador, ii_nombre
            from org.inst_interface join org.inst_elem
                on ie_id = ii_elemento 
            where ii_acceso is null
            and ii_elemento is not null
            and ii_perfil = perfil
            and ii_vista = vista
            union
            select ia_instalacion as inslatacion, ia_id, ia_nombre, ia_descripcion, ia_clase, ii_plantilla, ii_parametros, 'Acceso' as tipo, 3, ii_procesador, ii_nombre
