CREATE FUNCTION org.qjerarquiaelemento(inicio numeric) RETURNS TABLE("ieId" numeric, "iePadre" numeric, depth integer, cycle boolean)
    LANGUAGE plpgsql
    AS $$
begin

    return query
     WITH RECURSIVE jerarquia(ie_id, ie_padre, depth, path, cycle) AS (
        SELECT e1.ie_id, 
            r1.er_padre, 
            1,
            ARRAY[cast(e1.ie_id as integer)],
            false
        FROM org.inst_elem e1 left join org.inst_elem_rel r1
            on e1.ie_id = r1.er_hijo
        WHERE e1.ie_id = inicio
        UNION ALL
        SELECT e1.ie_id, 
            r1.er_padre, 
            sg.depth + 1,
            sg.path ||  cast(r1.er_hijo as integer),
            r1.er_hijo = any(sg.path)
        FROM org.inst_elem e1 left join 
             org.inst_elem_rel r1
                on e1.ie_id = r1.er_hijo
                join jerarquia sg
                on r1.er_hijo = sg.ie_padre
    )
    select j.ie_id, j.ie_padre, j.depth,  j.cycle
     from jerarquia as j
     UNION
     /* incorpora ultima fila */
    select j1.ie_padre, NULL, j1.depth + 1,  false
     from jerarquia as j1
     where j1.ie_padre not in (
        select ie_id
        from jerarquia
     );
     
end
$$;


