CREATE FUNCTION org.qjerarquiainstalacion(inicio numeric) RETURNS TABLE("insId" numeric, "insPadre" numeric, nivel integer, ruta integer[], ciclo boolean)
    LANGUAGE plpgsql
    AS $$
begin
    -- ejecuta la operacion almacenada en la variable dml
    return query
     WITH RECURSIVE jerarquia(ins_id, ins_padre, depth, path, cycle) AS (
        SELECT i1.ins_id, 
            i1.ins_padre, 
            1,
            ARRAY[cast(i1.ins_id as integer)],
            false
        FROM org.inst i1
        WHERE i1.ins_id = inicio
        UNION ALL
        SELECT i1.ins_id, 
            i1.ins_padre, 
            sg.depth + 1,
            sg.path ||  cast(i1.ins_id as integer),
            i1.ins_id = any(sg.path)
        FROM org.inst i1, jerarquia sg
        WHERE i1.ins_padre = sg.ins_id
            AND NOT sg.cycle
    )
    select j.ins_id, j.ins_padre, j.depth, j.path, j.cycle
     from jerarquia as j;
end
$$;


