CREATE FUNCTION org.qresidente(pid numeric, insid numeric) RETURNS TABLE("pId" numeric, rut character varying, "pSexo" character varying, "pTelefono" character varying, "pNombre" character varying, "pApellido" character varying, "pCorreo" character varying, "tipoResidente" json, "estacionamientosPropietario" json, "bodegasPropietario" json)
    LANGUAGE plpgsql
    AS $$
declare
    r1 RECORD;

    tmp_propietario numeric;
    tipos numeric[];
    estacionamientos character varying[];
    bodegas character varying[];
begin

    -- recupera tipos
    FOR r1 IN 
        select ic_rol from org.inst_contacto 
        where ic_instalacion = insId and ic_persona = pId
    LOOP
    BEGIN
        tipos = tipos || ARRAY[r1.ic_rol]; -- agrega elemento al arreglo
    END;
    END LOOP;

    -- recupera propietario
    SELECT ic_persona INTO tmp_propietario from org.inst_contacto WHERE ic_instalacion = insId AND ic_rol = 1600; -- 1600 rol propietario

    IF tmp_propietario IS NULL THEN
        tmp_propietario := -1;
    END IF;
    

    
    -- recupera estacionamientos
    FOR r1 IN 
        select ins_direccion
        from org.inst_contacto join org.inst
            on ic_instalacion = ins_id
        where ins_tipo = 1208 
        and ic_persona = tmp_propietario
        and ic_rol = 1600 -- propietario
    LOOP
    BEGIN
        estacionamientos = estacionamientos || ARRAY[r1.ins_direccion]; -- agrega elemento al arreglo
    END;
    END LOOP;

    -- recupera bodegas
    FOR r1 IN 
        select ins_direccion 
        from org.inst_contacto join org.inst
            on ic_instalacion = ins_id
        where ins_tipo = 1209 
        and ic_persona = tmp_propietario
        and ic_rol = 1600 -- propietario
    LOOP
    BEGIN
        bodegas = bodegas || ARRAY[r1.ins_direccion]; -- agrega elemento al arreglo
    END;
    END LOOP;


    return query

    SELECT p.p_id, coalesce(p."Rut.Valor",'null'), 
            coalesce(p."Sexo.Valor",'null'), 
            coalesce(p."Teléfono.Valor",'null'), 
            p.p_nombre, p.p_apellido, p.p_correo,  
            array_to_json(tipos), array_to_json(estacionamientos) , array_to_json(bodegas)
    from cac.vpersonapersonaatributo as p
    where p.p_id = pId;
            

end
$$;


