    LANGUAGE plpgsql
    AS $$
begin

    return query
    WITH q_firewall as (
        select ie_id, ie_tipo, ie_valor, ie_instalacion, 
            "IP.Valor",
            "Padre.Id"
        from org.velementoswseguridad 
        where ie_instalacion = inspadre
    ),
    q_qpserver as (
        select ie_id, ie_tipo, ie_valor, ie_instalacion, 
            "Websocket.Valor", 
             "Puerto.Valor", 
               "Protocolo.Valor",  
               "Orden.Valor", 
               "Version.Valor",  
               "Fecha_Instalacion.Valor", 
               "Nombre.Valor",
               "Json.Valor",
               "Modulo.Valor",
               "Padre.Id"
         from org.velementoswcomunicacionqp
    ),
    q_lan as (
        select ie_id, ie_tipo, ie_valor, ie_instalacion,
            "IP.Valor",
            "MAC.Valor",
            "Padre.Id"
         from org.velementotipocontrolador
    ),
    q_canales as (
        select ie_id, ie_tipo, ie_valor, ie_instalacion, 
            "Capacidad.Valor", 
               "Concurrencia.Valor",
               "Padre.Id"
         from org.velementoswelementosqp
    )

    select q_qpserver.ie_id, q_qpserver.ie_tipo, max(q_qpserver.ie_valor) as ie_valor, max(q_qpserver."Websocket.Valor") as websocket,
            COALESCE(max(q_lan."IP.Valor"), (SELECT MAX(q_lan."IP.Valor") FROM q_lan)) as server_ip, max(q_qpserver."Puerto.Valor") as puerto, 
            max(q_qpserver."Protocolo.Valor") as protocolo, max(q_qpserver."Orden.Valor") as orden,
            max(q_firewall."IP.Valor") as ip_wan, cast(max(q_qpserver."Json.Valor") as json) as parametros,
            max(q_qpserver."Modulo.Valor") as modulo,
            (select array_to_json(array_agg(t))
            from (
                select cast(q_canales.ie_valor as integer) as "Id", cast(q_canales."Capacidad.Valor" as integer) as "Capacidad.Valor", cast(q_canales."Concurrencia.Valor" as integer) as "Concurrencia.Valor"
                from q_canales
                where q_canales."Padre.Id" = q_qpserver.ie_id
            ) as t 
        )
    from  q_qpserver 
            left join q_firewall 
                on q_qpserver."Padre.Id" = q_firewall.ie_id
            left join q_lan 
                on q_qpserver."Padre.Id" = q_lan.ie_id
    where (q_qpserver.ie_instalacion = insid or -1 = insid)
    group by q_qpserver.ie_id, q_qpserver.ie_tipo
    order by orden; 

end
$$;


