CREATE FUNCTION org.regelemento(jsoninput jsonb, OUT salida boolean, OUT id numeric) RETURNS record
    LANGUAGE plpgsql
    AS $$

   DECLARE

    r1 record;
    r2 record;

    ieId numeric;
    ieInstalacion numeric;
    ieTipo numeric;

    ieValor  character varying;
    ieDescripcion  character varying;

    daId numeric;
    daAtributo numeric;
    daDominio numeric;
    valor character varying;

    hijo numeric;
    relacion numeric;
    
    
    BEGIN

    ieId := NULL;
    ieInstalacion := NULL;
    ieTipo := NULL;
    ieDescripcion := NULL;
    ieValor := false;


    
    IF jsonInput->>'ieId' IS NOT NULL THEN
        ieId := cast(jsonInput->>'ieId' as numeric);
    END IF;

    IF jsonInput->>'ieInstalacion' IS NOT NULL THEN
        ieInstalacion := cast(jsonInput->>'ieInstalacion' as numeric);
    END IF;

    IF jsonInput->>'ieTipo' IS NOT NULL THEN
        ieTipo := cast(jsonInput->>'ieTipo' as numeric);
    END IF;


    IF jsonInput->>'ieValor' IS NOT NULL THEN
        ieValor := jsonInput->>'ieValor';
    END IF;

    IF jsonInput->>'ieDescripcion' IS NOT NULL THEN
        ieDescripcion := jsonInput->>'ieDescripcion';
    END IF;

    
    IF ieId IS NULL THEN
    BEGIN

        INSERT INTO org.inst_elem(
            ie_descripcion, ie_doc, ie_estado, ie_fecha_inicio, ie_geo, ie_html, 
            ie_id, ie_instalacion, ie_propietario, ie_tipo, ie_valor)
        VALUES (ieDescripcion, 1, 50, CURRENT_TIMESTAMP, ' ', ' ', 
            NEXTVAL('org.seq_inst_elem'), ieInstalacion, 1, ieTipo, ieValor); 

        SELECT CURRVAL('org.seq_inst_elem') INTO ieId;

    END;
    ELSE
    BEGIN

        UPDATE org.inst_elem
           SET ie_descripcion=ieDescripcion, 
                ie_instalacion=ieInstalacion, 
                ie_tipo=ieTipo, 
            ie_valor=ieValor
         WHERE ie_id=ieId;
    END;
    END IF;
    

    -- elimina atributos
    delete from org.inst_elem_atr where  ela_elemento = ieId; 

    
    -- guardar atributos
    FOR r1 IN 
        select * from jsonb_array_elements(jsonInput->'atributos')
    LOOP
    BEGIN

        daAtributo := cast(r1.value->>'daAtributo' as numeric);
        daDominio := cast(r1.value->>'daDominio' as numeric);
        valor := cast(r1.value->>'valor' as character varying);
        
        IF r1.value->>'daId' IS NULL THEN 
        BEGIN
            SELECT da_id INTO daId from mmo.dominio_atributo  where da_atributo = daAtributo and da_dominio = daDominio;

            IF daId IS NULL THEN -- atributo no existe en el dominio
            BEGIN
                -- crear dominio atributo
                INSERT INTO mmo.dominio_atributo( da_atributo, da_dominio, da_id)
                        VALUES (daAtributo, daDominio, NEXTVAL('mmo.seq_dominio_atributo'));

                SELECT CURRVAL('mmo.seq_dominio_atributo') INTO daId;
            END;
            END IF;

        END; 
        ELSE
            daId := cast(r1.value->>'daId' as numeric);
        END IF; 
        
        INSERT INTO org.inst_elem_atr(  ela_atributo, ela_dominio_atributo, ela_elemento, ela_id, ela_valor )
            VALUES (daAtributo, daId, ieId, NEXTVAL('org.seq_inst_elem_atr'), valor);
        
    END;
    END LOOP;




    -- elimina hijos
    delete from org.inst_elem_rel where  er_padre = ieId; 

    -- guardar hijos
    FOR r2 IN 
        select * from jsonb_array_elements(jsonInput->'hijos')
    LOOP
    BEGIN

        hijo := cast(r2.value->>'hijo' as numeric);
        relacion := cast(r2.value->>'relacion' as numeric);
        

        INSERT INTO org.inst_elem_rel(er_hijo, er_id, er_padre, er_relacion)
            VALUES (hijo, NEXTVAL('org.seq_inst_elem_rel'), ieId, relacion); 
            

    END;
    END LOOP;

    
    id := ieId;
    
    salida := true;

          
    END;
    $$;


