    acceso numeric;
    usuario numeric;
    insId numeric;
    pDeptoId numeric;
    
    pId numeric;
    pRut character varying;
    pDv character varying;
    
    pNombre character varying;
    pApellido character varying;
    pFono character varying;
    pSexo numeric;
    pEmpresa numeric;
    observacion character varying;

    evId numeric;
    

    
    BEGIN

    evento := NULL;
    acceso := NULL;
    usuario := NULL;
    insId := NULL;
    pDeptoId := NULL;
    pId := NULL;
    pRut := NULL;
    pDv := NULL;
    pNombre := NULL;
    pApellido := NULL;
    pSexo := NULL;
    pFono := ' ';
    pEmpresa := NULL;
    observacion := NULL;

    IF jsoninput->>'evento' IS NOT NULL THEN
        evento := cast(jsoninput->>'evento' as numeric);
    ELSE
        RAISE EXCEPTION ' Error'
            USING HINT = 'Tipo de evento no valido';
    END IF;


    IF jsoninput->>'acceso' IS NOT NULL THEN
        acceso := cast(jsoninput->>'acceso' as numeric);
    END IF;

    IF jsoninput->>'usuario' IS NOT NULL THEN
        usuario := cast(jsoninput->>'usuario' as numeric);
    END IF;

    IF jsoninput->>'deptoId' IS NOT NULL THEN
        pDeptoId := cast(jsoninput->>'deptoId' as numeric);
    END IF;


    IF jsoninput->>'pRut' IS NOT NULL and char_length(jsoninput->>'pRut') > 0 THEN
        pRut := jsoninput->>'pRut';
    END IF;

    IF jsoninput->>'pDv' IS NOT NULL and char_length(jsoninput->>'pDv') > 0 THEN
        pDv := cast(jsoninput->>'pDv' as numeric);
    END IF;
    
    IF jsoninput->>'pId' IS NOT NULL and char_length(jsoninput->>'pId') > 0 THEN
        pId := cast(jsoninput->>'pId' as numeric);
    END IF;

    IF jsoninput->>'pNombre' IS NOT NULL and char_length(jsoninput->>'pNombre') > 0 THEN
        pNombre := jsoninput->>'pNombre';
    END IF;

    IF jsoninput->>'pApellido' IS NOT NULL and char_length(jsoninput->>'pApellido') > 0 THEN
        pApellido := jsoninput->>'pApellido';
    END IF;

    IF jsoninput->>'pSexo' IS NOT NULL and char_length(jsoninput->>'pSexo') > 0 THEN
        pSexo := jsoninput->>'pSexo';
    END IF;

    IF jsoninput->>'pFono' IS NOT NULL and char_length(jsoninput->>'pFono') > 0  THEN
