    END IF;

    IF jsoninput->>'pEmpresa' IS NOT NULL and char_length(jsoninput->>'pEmpresa') > 0  THEN
        pEmpresa := jsoninput->>'pEmpresa';
    END IF;

    IF jsoninput->>'observacion' IS NOT NULL and char_length(jsoninput->>'observacion') > 0  THEN
        observacion := jsoninput->>'observacion';
    END IF;

    RAISE NOTICE ' OK ';
    
    if ( evento = 550 or evento = 551 ) THEN
    BEGIN
        RAISE NOTICE ' OK ';
            
        IF evento != 551 THEN
        BEGIN
            pFono := ' ';
            pEmpresa := null;
        END;
        END IF;
        
        IF pId IS NULL THEN
        BEGIN
            IF pRut IS NULL  THEN
            BEGIN
                pRut = 1;
                pDv = '9';
            END;
            END IF;
        
            INSERT INTO cac.persona( p_apellido, p_correo, p_dv, p_empresa, p_id, p_nombre, p_rut, p_sexo, p_tratamiento, p_fono)
                    VALUES (pApellido, ' ', pDv, pEmpresa, NEXTVAL('cac.seq_persona'), pNombre, pRut, pSexo, 20, pFono); -- 311 masculino, 20 sr
            SELECT CURRVAL('cac.seq_persona') INTO pId;
        
        END;
        END IF;
        
        INSERT INTO org.evento( ev_acceso, ev_fecha, ev_gravedad, ev_id, ev_instalacion, ev_mensaje, ev_tipo, ev_usuario)
            VALUES (acceso, now(), 64, NEXTVAL('org.seq_evento'), pDeptoId, 1, evento, usuario);
        SELECT CURRVAL('org.seq_evento') INTO evId;

        INSERT INTO org.evento_wkf_step(evws_estado, evws_evento, evws_fec_acc, evws_fec_reg, evws_id, evws_observacion, evws_rol, evws_persona, evws_vigente)
            VALUES (780, evId, now(), now(), NEXTVAL('org.seq_evento_wkf_step'), observacion, 770, pId, 2);
        -- 780 informada
        -- 770 creador 
    END;
    ELSE
    BEGIN

        INSERT INTO org.evento( ev_acceso, ev_fecha, ev_gravedad, ev_id, ev_instalacion, ev_mensaje, ev_tipo, ev_usuario)
            VALUES (acceso, now(), 64, NEXTVAL('org.seq_evento'), pDeptoId, 1, evento, usuario);
        SELECT CURRVAL('org.seq_evento') INTO evId;

        INSERT INTO org.evento_wkf_step(evws_estado, evws_evento, evws_fec_acc, evws_fec_reg, evws_id, evws_observacion, evws_rol, evws_persona, evws_vigente)
            VALUES (780, evId, now(), now(), NEXTVAL('org.seq_evento_wkf_step'), observacion, 770, 1, 2);

    END;
    END IF;

    return 'true';
          
    END;
    $$;


