CREATE FUNCTION org.regresidente(jsonresidente jsonb, OUT salida boolean, OUT clave character varying, OUT persona character varying, OUT pid numeric) RETURNS record
    LANGUAGE plpgsql
    AS $$

   DECLARE

    
    --tmp_persona NUMERIC;
    tmpInsId numeric;
    tmpPropietarioId numeric;
    
    --login character varying;
    --num integer;
    esPropietario boolean;
    esMaestro boolean;




    insId numeric;
    pDeptoId numeric;
    pTorreId numeric;

    
    r1 record;
    r2 record;
    -- tmp record;

    tmpPersona numeric;
    tmpPropietario numeric;
    tmpUsuario numeric;

    tmpClave  character varying;
    tmpEstado numeric;

    datajson jsonb;
    
    --tmpPropietarioId numeric;
    
    
    BEGIN

    insId := NULL;
    pDeptoId := NULL;
    pTorreId := NULL;
    esPropietario := false;
    esMaestro := false;
    clave := '';
    persona := '';
    pId := NULL;

    
    IF jsonResidente->>'insId' IS NOT NULL THEN
        insId := cast(jsonResidente->>'insId' as numeric);
    END IF;


    IF jsonResidente->>'pDeptoId' IS NOT NULL THEN
        pDeptoId := cast(jsonResidente->>'pDeptoId' as numeric);
    END IF;
    

    SELECT cac.regpersona(jsonresidente) INTO tmpPersona;

    IF tmpPersona = -1 THEN
    BEGIN
        salida:= false;
        -- RETURN 'false';
    END;
    END IF;


    pId := tmpPersona;


    -- recuperar torre del departamento
    SELECT piso.ins_padre into pTorreId
    from org.inst as depto join org.inst as piso
        on depto.ins_padre = piso.ins_id
    where depto.ins_id = pDeptoId;
    


    -- elimina contacto de la instalacion
    delete from org.inst_contacto where ic_persona = tmpPersona and ic_instalacion = pDeptoId;
    
    -- guardar tipo de residente
    FOR r1 IN 
        select * from jsonb_array_elements_text(jsonResidente->'pTipo')
    LOOP
    BEGIN
        -- RAISE NOTICE ' % - ', r1.value;

        IF r1.value = '1600' THEN -- es propietario
            esPropietario := true;


            SELECT ic_persona INTO tmpPropietario 
                FROM org.inst_contacto 
            WHERE ic_instalacion = pDeptoId AND ic_rol = 1600;

            IF tmpPropietario IS NOT NULL THEN
            BEGIN

                -- elimina el propietario anterior  a residente
                DELETE FROM  org.inst_contacto 
                WHERE ic_instalacion = pDeptoId 
                    AND ic_rol = 1600
                    AND ic_persona = tmpPropietario;


                -- cambia bodegas y departamentos al nuevo propietario
                UPDATE org.inst_contacto 
                    SET ic_persona = tmpPersona
                WHERE ic_instalacion IN (
                    select ins_id
                    from org.inst join org.inst_contacto 
                        on ins_id = ic_instalacion
                    where ins_tipo IN ( 1208, 1209 )
                    and ic_persona = tmpPropietario 
                ) AND ic_rol = 1600;
                
            END;
            END IF;
            
            
        END IF;


        IF r1.value = '1603' THEN -- es maestro

        
            SELECT ic_persona INTO tmpPropietario 
                FROM org.inst_contacto 
            WHERE ic_instalacion = pDeptoId AND ic_rol = 1603;

            IF tmpPropietario IS NOT NULL THEN
            BEGIN

                -- elimina el maestro anterior  a residente
                DELETE FROM  org.inst_contacto 
                WHERE ic_instalacion = pDeptoId 
                    AND ic_rol = 1603
                    AND ic_persona = tmpPropietario;
            END;
            END IF;
            
            esMaestro := true;
            
        END IF;

        INSERT INTO org.inst_contacto(
             ic_id, ic_instalacion, ic_persona, ic_rol)
        VALUES (NEXTVAL('org.seq_inst_contacto'), pDeptoId, tmpPersona, cast(r1.value as numeric)); -- rol
            

    END;
    END LOOP;


    If esPropietario = true THEN
    BEGIN


        -- elimina estacionamientos/bodegas que puede tener el propietario
        FOR r2 IN 
            select ic_id
            from org.inst join org.inst_contacto 
                on ins_id = ic_instalacion
            where ins_tipo IN ( 1208, 1209 )
            and ic_persona = tmpPersona 
        LOOP
        BEGIN

            delete from org.inst_contacto where ic_id = r2.ic_id;

        END;
        END LOOP;



        -- guardar estacionamientos
        FOR r1 IN 
            select * from jsonb_array_elements_text(jsonResidente->'pEstacionamientos')
        LOOP

            -- recupera id de estacionamiento y verificar que no este tomado por otro usuario como propietario
            SELECT ii.ins_id, 
                (SELECT  ic_persona
                    FROM org.inst_contacto 
                WHERE ic_instalacion = ii.ins_id 
                    and ic_rol = 1600    -- rol propietario
                    LIMIT 1 ) INTO tmpInsId, tmpPropietarioId
            from org.inst ii
            where ii.ins_direccion = r1.value  
            and ii.ins_tipo = 1208;

            IF tmpPropietarioId IS NOT NULL THEN
            BEGIN
                -- error departamento tiene otro propietario
                IF tmpPropietarioId <> tmp_persona THEN
                    RAISE EXCEPTION 'Estacionamiento % tiene otro propietario', r1.value  
                        USING HINT = 'Verifique el numero de departamento';
                END IF;
            END;
            ELSE
            BEGIN

                IF tmpInsId IS NOT NULL THEN
                    -- registrar propietario
                    INSERT INTO org.inst_contacto(ic_id, ic_instalacion, ic_persona, ic_rol)
                    VALUES (NEXTVAL('org.seq_inst_contacto'), tmpInsId, tmpPersona, 1600); -- propietario
                ELSE
                    RAISE EXCEPTION 'Estacionamiento % no existe', r1.value  
                        USING HINT = 'Verifique el numero de departamento';

                END IF;
                
            END;
            END IF;

        END LOOP;



        -- guardar bodegas
        FOR r1 IN 
            select * from jsonb_array_elements_text(jsonResidente->'pBodegas')
        LOOP
            -- recupera id de estacionamiento y verificar que no este tomado por otro usuario como propietario
            SELECT bodega.ins_id, 
                (SELECT  ic_persona
                    FROM org.inst_contacto 
                WHERE ic_instalacion = bodega.ins_id 
                    and ic_rol = 1600    -- rol propietario
                    LIMIT 1 ) INTO tmpInsId, tmpPropietarioId
            from org.inst as bodega join org.inst as piso
                on bodega.ins_padre = piso.ins_id
            where bodega.ins_direccion = r1.value  
            and bodega.ins_tipo = 1209;

            IF tmpPropietarioId IS NOT NULL THEN
            BEGIN
                -- error departamento tiene otro propietario
                IF tmpPropietarioId <> tmpPersona THEN
                    RAISE EXCEPTION 'Bodega % tiene otro propietario', r1.value  
                        USING HINT = 'Verifique el numero de la bodega';
                END IF;
            END;
            ELSE
            BEGIN

                IF tmpInsId IS NOT NULL THEN
                    -- registrar propietario
                    INSERT INTO org.inst_contacto(ic_id, ic_instalacion, ic_persona, ic_rol)
                    VALUES (NEXTVAL('org.seq_inst_contacto'), tmpInsId, tmpPersona, 1600); -- propietario
                ELSE
                    RAISE EXCEPTION 'Bodega % no existe o no pertenece a la torre del departamento', r1.value  
                        USING HINT = 'Verifique el numero de la bodega';

                END IF;
                
            END;
            END IF;
        END LOOP;

    END;
    END IF;



    If esMaestro = true THEN
    BEGIN

        
        
            
        -- si hay algun maesttro anterior lo elimina
        DELETE FROM org.inst_contacto
            where ic_rol = 1603
            and ic_persona <> tmpPersona
            and ic_instalacion = pDeptoId;


        -- select jsonresidente || '{"pId": ' || cast(tmpPersona as character varying) || '}'::jsonb into datajson; 
        select jsonb_set(jsonresidente, '{pId}'::text[], cast(tmpPersona as character varying)::jsonb ) into jsonresidente;
        
        -- crea/recupera usuario
        select cac.regusuario(jsonresidente) INTO tmpUsuario;
        IF tmpUsuario <> -1 THEN
        BEGIN

            -- verificar si hay una clave pendiente o aprobada
            SELECT ul_estado INTO tmpEstado
            FROM cac.usuario_login 
                where ul_usuario = tmpUsuario 
                    AND ul_tipo = 863;

            -- si es nula o esta pendiente puede renovar la clave para validar nuevamnte
            IF tmpEstado is null or tmpEstado = 301 THEN
            BEGIN

                DELETE FROM cac.usuario_login 
                    WHERE ul_usuario = tmpUsuario 
                        AND ul_tipo = 863;
                
                -- crea logins de usuario con token de aplicacion
                -- 863 token de apolicacion
                -- 301 estado pendiente
                SELECT array_to_string(
                      ARRAY(
                          SELECT substring(
                        'abcdefghjkpqrstuvwxyzABCDEFGHIJKLMNPQRSTUVWXYZ123456789',
                        trunc(random()*62)::int+1,
                        1
                          )
                          FROM generate_series(1, 8) AS gs(x)
                      )
                      , ''
                    ) INTO tmpClave;


                
                INSERT INTO cac.usuario_login(ul_estado, ul_fecha_inicio, ul_fecha_termino, ul_id, 
                            ul_telefono, ul_tipo, ul_usuario, ul_valor)
                        VALUES ( 301, CURRENT_TIMESTAMP, NULL, NEXTVAL('cac.seq_usuario_login'), 
                            NULL, 863, tmpUsuario, tmpClave);

                -- selecciona nombre para correo electronico
                select pNombre into persona from cac.persona where p_id = tmpPersona;
                clave:= tmpClave;
            END;
            END IF; 

            
            
        END;
        END IF;



        

    END;
    END IF;

    
    salida:= true;
    -- return 'true';
          
    END;
    $$;


