CREATE FUNCTION org.tiempo_del() RETURNS trigger
    LANGUAGE plpgsql
    AS $$
declare
 tId numeric;
  tejecutada numeric;
   tarchivo numeric;
 dmlsqlite text;
begin

    -- no informa transacciones negativas a sincronizar
    if(old.tmp_id < 0) then
        return null;
    end if;

    dmlsqlite := concat('delete from ''org.tiempo'' where tmp_id=', old.tmp_id, ';');
    tejecutada := 0;
    tarchivo := 0;
    select nextval('trx.seq_trans')::regclass into tId;
    -- inserta la transaccion en la tabla trans
    insert into trx.trans(t_id, t_sql, t_archivo, t_hora, t_historial, t_ejecutada) 
    values(tId, dmlsqlite, tarchivo, now(), 0, tejecutada);
    return old;
