CREATE FUNCTION org.tradusuario(usu numeric, per numeric DEFAULT NULL::numeric, ele numeric DEFAULT NULL::numeric) RETURNS text
    LANGUAGE plpgsql
    AS $$
declare descUsuario TEXT;
begin
    if Per is not null then 
        SELECT PERSONA.P_NOMBRE  || ' ' ||  PERSONA.P_APELLIDO INTO descUsuario  FROM cac.PERSONA WHERE P_ID = Per;
    else
        if ele is not null then 
            SELECT IE_DESCRIPCION INTO descUsuario FROM org.INST_ELEM WHERE IE_ID = ele;
        else
            SELECT cac.USUARIO.U_LOGIN INTO descUsuario FROM cac.USUARIO WHERE cac.USUARIO.U_ID = Usu;
        end if;
    end if;
    return descUsuario;
end;
