INSERT INTO doc.det (dd_ambito, dd_anterior, dd_autor, dd_categoria, dd_origen, dd_codigo, dd_cuerpo_base64, dd_encabezado, dd_estado, dd_fecha_hora_creacion, dd_id, dd_idioma, dd_medio_destino, dd_nombre, dd_prioridad, dd_publicacion_base64, dd_resumen_base64, dd_seccion, dd_siguiente, dd_visible, dd_tipo) VALUES (136, NULL, 2, 281, 0, 'jparra_08-08-2021 07:43:33', '', 2000, 160, '2021-08-08 07:43:33', 1696, 10, 157, 'Política de Calidad', 200, '<h2><span class="text-big">Garantizar una alta disponibilidad del Servicio cuando el cliente la requiera&nbsp;</span></h2><p>&nbsp;</p><p><span class="text-big">Incluido offline, de manera que se sincronice en forma automática sin que el cliente lo perciba</span><br><span class="text-big">Cumplir con los requerimiento del usuario y de acuerdo al Alcance contratado</span><br><span class="text-big">Lograr un nivel alto de satisfacción de manera que nos recomiende (NPS)</span><br><span class="text-big">Servicio de Soporte Continuo, Canal de Comunicación permanente con el cliente (incluidos Bots, Chats)</span></p>', 'Garantizar una alta disponibilidad del Servicio cuando el cliente la requiera ', 187, NULL, 1, 948);
INSERT INTO doc.det (dd_ambito, dd_anterior, dd_autor, dd_categoria, dd_origen, dd_codigo, dd_cuerpo_base64, dd_encabezado, dd_estado, dd_fecha_hora_creacion, dd_id, dd_idioma, dd_medio_destino, dd_nombre, dd_prioridad, dd_publicacion_base64, dd_resumen_base64, dd_seccion, dd_siguiente, dd_visible, dd_tipo) VALUES (136, NULL, 2, 281, 0, 'jparra_08-08-2021 07:27:38', '', 1998, 160, '2021-08-08 07:27:38', 1694, 10, 157, 'Misión', 200, '<p>&nbsp;</p><h2><span class="text-big">Ofrecer un servicio que permita un acceso universal a tecnologías worldclass</span></h2><p>&nbsp;</p><p><span class="text-big">¿<strong>Qué necesidad satisfacemos</strong>?</span><br><span class="text-big">Facilitar &nbsp;la Gestión de las Empresas</span><br><span class="text-big">Compartir las Mejores Prácticas</span><br><span class="text-big">Modelación de Procesos de Negocio (ejemplo: Sistema en línea de ordenes de pedidos, catalogo de productos, carrito de compras, notificación de ventas, motor de pago).&nbsp;</span></p><p><span class="text-big">Modulo de Encuestas. Modulo de Desarrollo de Carrera. Firma Electrónica para Empresas.</span></p><p><br><span class="text-big">¿<strong>Cómo</strong>?</span><br><span class="text-big">Proveedor de Soluciones para PYMES, democratización el acceso a teconologías worldclass</span><br><span class="text-big">Solución general que aplique a muchos clientes (PxQ)</span><br><span class="text-big">Gerencia de TI externa (Servicios Compartidos)</span></p><p><br><span class="text-big">¿<strong>A quién va dirigido</strong>?</span><br><span class="text-big">PYMES</span><br><span class="text-big">Nicho de Personas (Ejemplo comunidad de Windsurf, IOT para Condominios - Seguridad)</span><br><span class="text-big">Inter Pymes (Comunidades de Negocios, Market Place)</span></p><p>&nbsp;</p>', 'Ofrecer un servicio que permita un acceso universal a tecnologías worldclass', 187, NULL, 1, 948);
INSERT INTO doc.det (dd_ambito, dd_anterior, dd_autor, dd_categoria, dd_origen, dd_codigo, dd_cuerpo_base64, dd_encabezado, dd_estado, dd_fecha_hora_creacion, dd_id, dd_idioma, dd_medio_destino, dd_nombre, dd_prioridad, dd_publicacion_base64, dd_resumen_base64, dd_seccion, dd_siguiente, dd_visible, dd_tipo) VALUES (136, NULL, 2, 281, 0, 'jparra_08-08-2021 07:41:24', '', 1999, 160, '2021-08-08 07:41:24', 1695, 10, 157, 'Alcance del SGC', 200, '<h2><span class="text-big">Soluciones Tecnológicas dirigidas a Pequeñas y Medianas Empresas</span></h2><p>&nbsp;</p><p><span class="text-big">¿<strong>Que procesos operacionales vamos a estandarizar</strong>?</span></p><p><br><span class="text-big">Proceso de Desarrollo de Plataformas Verticales (ISO-APP escalable)</span><br><span class="text-big">Proceso Ventas (Servicios SAAS con Acuerdo SLA)</span><br><span class="text-big">Proceso de Levantamiento</span><br><span class="text-big">Proceso de Implementación</span><br><span class="text-big">Proceso de Post venta (Soporte)</span></p><p>&nbsp;</p>', 'Soluciones Tecnológicas dirigidas a Pequeñas y Medianas Empresas', 187, NULL, 1, 948);
INSERT INTO doc.det (dd_ambito, dd_anterior, dd_autor, dd_categoria, dd_origen, dd_codigo, dd_cuerpo_base64, dd_encabezado, dd_estado, dd_fecha_hora_creacion, dd_id, dd_idioma, dd_medio_destino, dd_nombre, dd_prioridad, dd_publicacion_base64, dd_resumen_base64, dd_seccion, dd_siguiente, dd_visible, dd_tipo) VALUES (136, NULL, 2, 281, 0, 'jparra_08-08-2021 07:45:37', '', 2001, 160, '2021-08-08 07:45:37', 1697, 10, 157, 'Valores Corporativos', 200, '<p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">1. Etica, trabajar bajo este concepto&nbsp;</span></p><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">2. Protección de la información del Cliente (Confidencialidad)&nbsp;</span></p><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">3. Cumplir el marco legal de seguridad de la información nacional e internacional&nbsp;</span></p><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">4. Confianza por medio de profesionalismo en la gestión de la información de nuestros clientes (Canales de Comunicación Formal, ejemplo el área de desarrollo no se puede relacionar directamente con clientes)&nbsp;</span></p><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">5. Trabajar internamente bajo el concepto de confianza&nbsp;</span></p><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">6. Crecimiento Sostenible (en base a incorporación de más personas o aliados estratégicos)&nbsp;</span></p><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">7. Calidad de Vida en equilibro con el trabajo que desarrollamos (tener tiempo, no estresarnos en extremo, nivel de stress tolerable)&nbsp;</span></p><p><span style="background-color:rgb(255,255,255);color:rgb(0,0,0);">8. Apoyo continuo (no dejar botado a nuestros clientes). Lograr relaciones de Largo Plazo (Fidelización).</span></p>', 'Conjunto de principios éticos y profesionales que orientan las acciones y actitudes de la empresa', 187, NULL, 1, 948);
